export const starRender = (stars) => {
  if (stars === 1) {
    return (
      <>
      <i className="fa-solid fa-star"></i>
      </>
    )
  }
  if (stars === 2) {
    return (
      <>
      <i className="fa-solid fa-star"></i>
      <i className="fa-solid fa-star"></i>
      </>
    )
  }
  if (stars === 3) {
    return (
      <>
      <i className="fa-solid fa-star"></i>
      <i className="fa-solid fa-star"></i>
      <i className="fa-solid fa-star"></i>
      </>
    )
  }
  if (stars === 4) {
    return (
      <>
      <i className="fa-solid fa-star"></i>
      <i className="fa-solid fa-star"></i>
      <i className="fa-solid fa-star"></i>
      <i className="fa-solid fa-star"></i>
      </>
    )
  }
  if (stars === 5) {
    return (
      <>
      <i className="fa-solid fa-star"></i>
      <i className="fa-solid fa-star"></i>
      <i className="fa-solid fa-star"></i>
      <i className="fa-solid fa-star"></i>
      <i className="fa-solid fa-star"></i>
      </>
    )
  }
}
